-- Create "patient_appointment_sign_ups" table
CREATE TABLE "reorder"."patient_appointment_sign_ups" ("id" serial NOT NULL, "appointment_id" integer NOT NULL, "user_id" integer NOT NULL, "comment" text NULL, PRIMARY KEY ("id"), CONSTRAINT "patient_appointment_sign_ups_appointment_id_user_id_key" UNIQUE ("appointment_id", "user_id"), CONSTRAINT "patient_appointment_sign_ups_appointment_id_fkey" FOREIGN KEY ("appointment_id") REFERENCES "reorder"."patient_appointments" ("id") ON UPDATE NO ACTION ON DELETE NO ACTION, CONSTRAINT "patient_appointment_sign_ups_user_id_fkey" FOREIGN KEY ("user_id") REFERENCES "reorder"."users" ("id") ON UPDATE NO ACTION ON DELETE NO ACTION);
